<!DOCTYPE html><html lang="zh-CN"><head>    <meta charset="utf-8">    <meta http-equiv="X-UA-Compatible" content="IE=edge">    <meta name="viewport" content="width=device-width, initial-scale=1">    <meta name="description" content="">    <meta name="author" content="">    <title>商品管理</title>    <link rel="stylesheet" href="/css/content.css" />    <link rel="stylesheet" href="/css/upload.css" />    <link rel="stylesheet" href="/js/layui/css/layui.css">    <link rel="stylesheet" type="text/css" href="/js/webuploader-0.1.5/webuploader.css">    <link rel="stylesheet" href="//cdn.bootcss.com/bootstrap/3.3.5/css/bootstrap.min.css">    <script src="/js/jquery.min.js?v=2.1.4"></script>    <script src="/js/layui/layui.js"></script>    <script src="//cdn.bootcss.com/bootstrap/3.3.5/js/bootstrap.min.js"></script>    <script type="text/javascript" src="/js/webuploader-0.1.5/webuploader.js"></script>    <script type="text/javascript" src="/js/global.js"></script></head><style>    .panel-primary{        border-color: #1ab394;    }    a{        text-decoration: none !important;    }    .layui-form-label{        width:auto;    }    .layui-form-item{        margin-bottom: 30px;    }    .unit{        position: absolute;        top:15px;        margin-left:195px;    }    .ipt-red{        color:red    }</style><body><div class="container-fluid" style="margin-top: 10px">    <div class="panel panel-primary">        <div class="panel-heading" style="background-color: #1ab394;border-color: #1ab394;">            <span class="badge" style=" color: #1ab394;" >添加商品图片</span>        </div>    </div></div><div class="container-fluid" id="add-manage-popup" style="margin-left:20px;margin-top:20px" >    <form class="layui-form" action="">        <div class="layui-form-item">            <label class="layui-form-label"><span class='ipt-red'>*</span>商品名称</label>            <div class="layui-input-inline" >                <select name="quiz3" lay-verify=""  id="menuup" >                    <option value="0">请选择商品</option>                    <?php foreach($products as $val):?>                    <option value="<?php echo $val->id?>"><?php echo $val->id.'.'.$val->name?></option>                    <?php endforeach;?>                </select>            </div>        </div>        <div id="wrapper">            <div id="container">                <!--头部，相册选择和格式选择-->                <div id="uploader">                    <div class="queueList">                        <div id="dndArea" class="placeholder">                            <div id="filePicker"></div>                            <p>或将照片拖到这里，单次最多可选6张</p>                        </div>                    </div>                    <div class="statusBar" style="display:none;">                        <div class="progress">                            <span class="text">0%</span>                            <span class="percentage"></span>                        </div><div class="info"></div>                        <div class="btns">                            <div id="filePicker2"></div><div class="uploadBtn">开始上传</div>                        </div>                    </div>                </div>            </div>        </div>        <div class="layui-form-item" style="margin-left:100px; " >            <div class="layui-btn layui-btn-big" lay-submit="" lay-filter="demo2" id="sub-btn" style="margin-top: 20px;">保存</div>            <input name="action" type="hidden" class="layui-input" value="" id="action">            <input name="eid" type="hidden" class="layui-input" value="{{$product['product']->id}}" id="eid">        </div>    </form></div></body><script>    $(function () {        //上传图片        //globalFn.webUpload('/product/upload','{{csrf_token()}}','show-img', 'pimgurl');        globalFn.webUploadMulit('/product/upload','{{csrf_token()}}','show-img', 'pimgurl');        //预览        //globalFn.preview('show-img');        layui.use(['form', 'layedit', 'laydate'], function(){            var form = layui.form,layer = layui.layer;            //监听提交            form.on('submit(demo2)', function(data){                if(!$('.layui-form-checkbox').hasClass('layui-form-checked')){                    layer.msg('请选择分类');                    return false;                }                if($('#pname').val() == ''){                    layer.msg('请输入商品名称');                    return false;                }                if($('#pimgurl').val() == ''){                    layer.msg('请上传商品封面图片');                    return false;                }                if($('#psprice').val() == ''){                    layer.msg('请输入销售价格');                    return false;                }                if($('#pstore').val() == ''){                    layer.msg('请输入商品库存');                    return false;                }                _index = layer.load();                $.post("{{route('product.create')}}", {'_token':'{{csrf_token()}}',data:JSON.stringify(data.field)},function(res){                    res.status == 1 ? globalFn.remind(_index, res.msg,"{{route('product.index')}}")                        : globalFn.remind(_index, res.msg);                });                return false;            });        });    });    function clearNoNum(obj){        obj.value = obj.value.replace(/[^\d.]/g,"");  //清除“数字”和“.”以外的字符        obj.value = obj.value.replace(/\.{2,}/g,"."); //只保留第一个. 清除多余的        obj.value = obj.value.replace(".","$#$").replace(/\./g,"").replace("$#$",".");        obj.value = obj.value.replace(/^(\-)*(\d+)\.(\d\d).*$/,'$1$2.$3');//只能输入两个小数        if(obj.value.indexOf(".")< 0 && obj.value !=""){//以上已经过滤，此处控制的是如果没有小数点，首位不能为类似于 01、02的金额            obj.value= parseFloat(obj.value);        }else if(obj.value.length == 1){            obj.value= '';        }    }</script></html>